<?php include 'conexao.php';$r=$conn->query('SELECT * FROM produtos');echo "<a href='produto_adicionar.php'>Novo</a><table><tr><th>ID</th><th>Nome</th><th>Preço</th><th>Qtd</th><th>Desc</th><th>Ações</th></tr>";while($p=$r->fetch_assoc()){echo "<tr><td>{$p['id']}</td><td>{$p['nome']}</td><td>{$p['preco']}</td><td>{$p['quantidade']}</td><td>{$p['descricao']}</td><td><a href='produto_editar.php?id={$p['id']}'>Editar</a> <a href='produto_excluir.php?id={$p['id']}'>Excluir</a></td></tr>";}echo "</table>";?>